"use client";

import type React from "react";
import { motion } from "framer-motion";
import { cn } from "@/lib/utils";

interface HighlightedArticleProps {
  title: string;
  description: string;
  image: string;
  date: string;
  author: {
    name: string;
    avatar?: string;
  };
  className?: string;
}

export const HighlightedArticle: React.FC<HighlightedArticleProps> = ({
  title,
  description,
  image,
  date,
  author,
  className,
}) => {
  return (
    <motion.article
      className={cn(
        "relative overflow-hidden rounded-lg bg-card p-6",
        "transform-gpu transition-all duration-300",
        className
      )}
      whileHover={{ scale: 1.02 }}
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
    >
      <div className="relative aspect-video overflow-hidden rounded-md mb-4">
        <motion.img
          src={image}
          alt={title}
          className="object-cover w-full h-full"
          whileHover={{ scale: 1.1 }}
          transition={{ duration: 0.3 }}
        />
      </div>

      <div className="space-y-2">
        <motion.h2
          className="text-xl font-semibold tracking-tight"
          whileHover={{ x: 10 }}
        >
          {title}
        </motion.h2>

        <p className="text-muted-foreground">{description}</p>

        <div className="flex items-center gap-4 text-sm text-muted-foreground">
          <div className="flex items-center gap-2">
            {author.avatar && (
              <img
                src={author.avatar}
                alt={author.name}
                className="w-6 h-6 rounded-full"
              />
            )}
            <span>{author.name}</span>
          </div>
          <time dateTime={date}>{new Date(date).toLocaleDateString()}</time>
        </div>
      </div>
    </motion.article>
  );
};
